def change(x):
    if x == 'A':
        return 'T'
    elif x == 'T':
        return 'A'
    elif x == 'C':
        return 'G'
    elif x == 'G':
        return 'C'
    return x

def rev_comp(s):
    u=[change(x) for x in s]
    u.reverse()
    return ''.join(u)

