def trouve_is_autour(item):
    with open('/tmp/CASs.fasta','w') as f:
        for k in genes_cas:
            cpt,mot = 0,50
            for l in range(0,len(genes_cas[k]),mot):
                if len(genes_cas[k][l:])>=mot/2:
                    f.write(f">{k+str(cpt)}\n{genes_cas[k][l:l+mot]}\n")
                    cpt += 1
            for l in range(mot//2,len(genes_cas[k]),mot):
                if len(genes_cas[k][l:])>=mot/2:
                    f.write(f">{k+str(cpt)}\n{genes_cas[k][l:l+mot]}\n")
                    cpt += 1


    completed = subprocess.run("blastn -query /tmp/CASs.fasta -task blastn -evalue 1e-3 -db "+item+" -num_threads 8 -outfmt '10 sseqid sstart send' -out /tmp/"+item+'_cas', shell = True)
    assert completed.returncode == 0

    seqs = {}
    with open('/tmp/'+item+'_cas', 'r') as f:
        for u in f.read().split('\n')[:-1]:
            nom, deb,fin = u.split(',')
            deb,fin = eval(deb),eval(fin)
            seqs[nom] = deb<fin

    from Bio import pairwise2, SeqIO
    fasta_sequences = {}
    SEQS = []
    for u in ['_1', '_2']:
        fasta_sequences[item+u] = SeqIO.parse(open(rep+item+u+'.fasta'),'fasta')
    for sens in fasta_sequences:
        for fasta in fasta_sequences[sens]:
            if fasta.id in seqs:
                if seqs[fasta.id]:
                    SEQS.append(str(fasta.seq))
                else:
                    SEQS.append(rev_comp(str(fasta.seq)))

    is_autour = {}
    for k in SEQS:
        for l in dico_IS:
            debut = dico_IS[l][:15]
            if debut in k:
                prefixe = k.split(debut)[0]
                for m in genes_cas:
                    if prefixe in genes_cas[m]:
                        desc = f"{m}[:{genes_cas[m].find(prefixe)+len(prefixe)}]+{l}"
                        if desc not in is_autour:
                            is_autour[desc] = 1
                        else:
                            is_autour[desc] += 1
            fin = dico_IS[l][-15:]
            if fin in k:
                suffixe = k.split(fin)[1]
                for m in genes_cas:
                    if suffixe in genes_cas[m]:
                        desc = f"{l}+{m}[{genes_cas[m].find(suffixe)+len(suffixe)}:]"
                        if desc not in is_autour:
                            is_autour[desc] = 1
                        else:
                            is_autour[desc] += 1

    print("\n".join([f"{k:<40} : {is_autour[k]:>10} fois" for k in is_autour]))

